CREATE TABLE IF NOT EXISTS artistes (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    nom TEXT NOT NULL,
    photo TEXT,
    dateNaissance TEXT,
    dateDeces TEXT,
    styleArt TEXT
);

CREATE TABLE IF NOT EXISTS musees (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    logo TEXT,
    nom TEXT NOT NULL,
    date_creation TEXT,
    adresse TEXT
);

CREATE TABLE IF NOT EXISTS oeuvres (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    nom TEXT NOT NULL,
    photo TEXT,
    date_creation TEXT,
    auteur TEXT,
    musee TEXT
);





